Rules.InvariantScript:
"<div
    style='
        margin:10px;
        width: "& Self.Width - 40 &"px;
        height: "& Self.Height - 40 &"px;
        background:
            "&If(
                'Date Picker'.DarkMode=true,
                "linear-gradient(135deg, rgba(36,36,44,1) 0%, rgba(52,57,61,1) 100%)",
                "#e8eaec"
            )&";
        box-shadow:
            "&If(
                'Date Picker'.DarkMode =true,
                "inset 3px 3px 3px 0px #1e2126,
                inset -3px -3px 3px 0px rgba(61,61,69,1)",
                "6px 6px 8px rgba(13, 39, 80, 0.25),
                -6px -6px 6px white,
                inset -8px -8px 12px rgba(255, 255, 255, 0.7),
                inset 5px 5px 8px rgba(13, 39, 80, 0.2)" 
            )&";
        border-radius:10px'>
</div>"

/*resets calendar view and date selection to today*/
Set(
    _firstDayOfMonth,
    DateAdd(
        Today(),
        1 - Day(Today()),
        TimeUnit.Days
    )
);
Set(
    _firstDayInView,
    DateAdd(
        _firstDayOfMonth,
        -(Weekday(_firstDayOfMonth) - 2 + 1),
        TimeUnit.Days
    )
);
Set(
    _lastDayOfMonth,
    DateAdd(
        DateAdd(
            _firstDayOfMonth,
            1,
            TimeUnit.Months
        ),
        -1,
        TimeUnit.Days
    )
);

Set(varCalendarVis, !varCalendarVis)

"<div
    style='
        margin:30px;
        width: "& Self.Width - 80 &"px;
        height: "& Self.Height - 80 &"px;
        background:
            "&If(
                'Date Picker'.DarkMode = true,
                "linear-gradient(135deg, rgba(36,36,44,1) 0%, rgba(52,57,61,1) 100%)",
                "#e8eaec"
            )&";
        box-shadow:
            "&If(
                'Date Picker'.DarkMode = true,
                "inset 3px 3px 3px 0px #1e2126, inset -3px -3px 3px 0px rgba(61,61,69,1)",
                "12px 12px 32px rgba(13, 39, 80, 0.25), -10px -10px 15px white, inset -15px -15px 20px rgba(255, 255, 255, 0.7), inset 8px 8px 20px rgba(13, 39, 80, 0.2)"
            )&";
        border-radius:25px'
>
</div>"

/*changes month view to previous month*/
Set(_firstDayOfMonth, DateAdd(_firstDayOfMonth, -1, TimeUnit.Months));
Set(_firstDayInView, DateAdd(_firstDayOfMonth, -(Weekday(_firstDayOfMonth) - 2 + 1), TimeUnit.Days));
Set(_lastDayOfMonth, DateAdd(DateAdd(_firstDayOfMonth, 1, TimeUnit.Months), -1, TimeUnit.Days));

/*changes month view to next month*/
Set(_firstDayOfMonth, DateAdd(_firstDayOfMonth, 1, TimeUnit.Months));
Set(_firstDayInView, DateAdd(_firstDayOfMonth, -(Weekday(_firstDayOfMonth) - 2 + 1), TimeUnit.Days));
/*collects calendar events for all days in current month view. Updates _maxDate to prevent duplicate data collection if user returns to this month view*/
Set(_lastDayOfMonth, DateAdd(DateAdd(_firstDayOfMonth, 1, TimeUnit.Months), -1, TimeUnit.Days));
Set(_maxDate, _lastDayOfMonth);

"<div
    style='
        margin:7px 7px;
        width: 35px;
        height:35px;
        background:
            "&If(
                'Date Picker'.DarkMode = true,
                "linear-gradient(135deg, rgba(36,36,44,1) 0%, rgba(52,57,61,1) 100%)",
                "#e8eaec"
            )&";
        box-shadow:
            "&If(
                'Date Picker'.DarkMode = true,
                "inset 3px 3px 3px 0px #1e2126, inset -3px -3px 3px 0px rgba(61,61,69,1)",
                "inset 4px 4px 3px #bec0c2,inset -4px -4px 3px #ffffff"
            )&";
        border-radius:10px
    '
>
        "&If(
            And(
                DateAdd(
                    _firstDayInView,
                    ThisItem.Value
                )
                =_dateSelected,
                MDG_Label.Visible
            ),
            "<div
                style='
                    position:absolute;
                    transform:translate(8px, 28px);
                    width: 20px;
                    height:3px;
                    background:linear-gradient(to right, "&'Date Picker'.IconLinealGradient1&", "&'Date Picker'.IconLinealGradient2&") ;
                    box-shadow: "&'Date Picker'.IconLinealGradient2&"  0px 2px 9px,inset -3px -3px 7px "&'Date Picker'.IconLinealGradient2&" ;
                    border-radius: 90px'
            </div>",
            ""
        )&"
</div>"

/*Fill value changes if the gallery item day = today, or if it lies outside of the current month*/             
If(
    /*Date selected is today*/
    DateAdd(_firstDayInView, ThisItem.Value) = Today() && DateAdd(_firstDayInView, ThisItem.Value) = _dateSelected, RGBA(0,0,0,0),
    /*Today when it is not selected*/
    DateAdd(_firstDayInView, ThisItem.Value) = Today(), Color.Transparent, 
    /*The day is outside the range of the currently selected month*/
    Abs(Self.Text - ThisItem.Value) > 10,RGBA(200, 200, 200, 0.3), RGBA(0, 0, 0, 0))
