Rules.InvariantScript:
"<div
    style='
        margin:15px;
        width: "& Parent.Width -60 &"px;
        height: "& Parent.Height - 60 &"px;
        background: "&If(
            LinearGradient_Generator_HEX.DarkMode = true,
            "linear-gradient(135deg, rgba(36,36,44,1) 0%, rgba(52,57,61,1) 100%)",
            "#e8eaec"
        )&";
        box-shadow:
            "&If(
                LinearGradient_Generator_HEX.DarkMode = true,
                "-8px -8px 10px 0px rgba(61,61,69,1), 8px 8px 10px 0px #1e2126",
                "12px 12px 32px rgba(13, 39, 80, 0.25),-10px -10px 15px white"
            )&";
        border-radius:20px'
>
</div>"

"<div
    style='
        margin:8px;
        height: "& Self.Height/2.4 &"px;
        border-radius: 15px;
        background: "&If(
            LinearGradient_Generator_HEX.DarkMode = true,
            "linear-gradient(135deg, rgba(36,36,44,1) 0%, rgba(52,57,61,1) 100%)",
            "#e8eaec"
        )&";
        border: "&If(
            LinearGradient_Generator_HEX.DarkMode = true,
            "4px solid #2f3035",
            "4px solid rgba(255, 255, 255, 0.7)"
        )&";
        box-shadow: "&If(
            LinearGradient_Generator_HEX.DarkMode = true,
            "-2px -2px 4px 0px rgba(61,61,69,1), 2px 2px 10px 0px #1e2126;",
            "2px 2px 2px rgba(13, 39, 80, 0.25),
            -2px -2px 10px white,
            inset -4px -4px 12px rgba(255, 255, 255, 0.7),
            inset 2px 2px 3px rgba(13, 39, 80, 0.2)"
        )&";
'>


    <div
        style='
            width:100%;
            height: 100%;
            background:linear-gradient(
                to right,
                "&"#"& T_TextInput_1.Text&",
                "&"#"& T_TextInput_2.Text&"
            );
            border-radius: 8px;
        '>
    </div>
</div>"

"<div
    style='
        margin:10px;
        width: "& T_HTML_1.Width - 40 &"px;
        height: "& T_HTML_1.Height - 40 &"px;
        background: "&If(
            LinearGradient_Generator_HEX.DarkMode = true,
            "linear-gradient(135deg, rgba(36,36,44,1) 0%, rgba(52,57,61,1) 100%)",
            "#e8eaec"
        )&";
        box-shadow:
            "&If(
                LinearGradient_Generator_HEX.DarkMode = true,
                "-8px -8px 10px 0px rgba(61,61,69,1),
                8px 8px 10px 0px #1e2126,
                inset 3px 3px 3px 0px #1e2126,
                inset -3px -3px 3px 0px rgba(61,61,69,1)",
                "6px 6px 15px rgba(13, 39, 80, 0.25),
                -5px -5px 11px white,
                inset -15px -15px 20px rgba(255, 255, 255, 0.7),
                inset 8px 8px 30px rgba(13, 39, 80, 0.2)"
            )&";
        border-radius:10px'
>
    "& If(
        varIsSelected = true,
        "<div
            style='
            position:absolute;
            transform:translate(9%);
            width: "& (Self.Width -45)/1.2  &"px;
            height:3px;
            background:linear-gradient(to right,
                                        "& LinearGradient_Generator_HEX.IconLinealGradient1 &",
                                        "& LinearGradient_Generator_HEX.IconLinealGradient2 &"
                                    );
            box-shadow: "&LinearGradient_Generator_HEX.IconLinealGradient1 &"  0px 2px 9px,inset -3px -3px 7px "& LinearGradient_Generator_HEX.IconLinealGradient2 &" ;                                border-radius: 36px'
        </div>",
        ""
    )&"
</div>"

