Rules.InvariantScript:
"<div
    style='
        margin:0px;
        width: "& MNM_HTML.Width - 30 &"px;
        height: "& MNM_HTML.Height - 30 &"px;
        background:
            "&If(
                'Mobile Navigation Menu'.DarkMode = true,
                "linear-gradient(135deg, rgba(36,36,44,1) 0%, rgba(52,57,61,1) 100%)",
                "#e8eaec"
            )&";
        box-shadow:
            "&If(
                'Mobile Navigation Menu'.DarkMode = true,
                "-1px -1px 0px 0px rgba(61,61,69,1), 8px 8px 10px 0px #1e2126",
                "6px 6px 12px rgba(13, 39, 80, 0.25), -1px -1px 6px white"
            )&";
        border-radius:25px
    '
>
</div>"

"<div
    style='
        margin:10px 10px;
        width: "& MNM_Gallery.TemplateWidth - 30 &"px;
        height: "& MNM_Gallery.TemplateHeight - 40 &"px;
        background: "&If(
            'Mobile Navigation Menu'.DarkMode = true,
            "linear-gradient(135deg, rgba(36,36,44,1) 0%, rgba(52,57,61,1) 100%)",
            "#e8eaec"
        )&";
        box-shadow:
            "&If(
                'Mobile Navigation Menu'.DarkMode = true,
                "-3px -4px 10px 0px rgba(61,61,69,1), 4px 4px 10px 0px #1e2126",
                "1px 6px 11px rgba(13, 39, 80, 0.5), -1px -1px 4px white, inset -12px -13px 20px rgba(255, 255, 255, 0.7), inset 8px 18px 23px rgba(13, 39, 80, 0.2)"
            )&";
        border-radius:25px
    '
>
</div>"

