Rules.InvariantScript:
"<div
    style='
        margin:10px;
        width: "& N_HTML.Width - 40 &"px;
        height: "& N_HTML.Height -35&"px;
        background:
            "&If(
                Navigation.DarkMode = true,
                "linear-gradient(135deg, rgba(36,36,44,1) 0%, rgba(52,57,61,1) 100%)",
                "#e8eaec"
            )&";
        box-shadow:
            "&If(
                Navigation.DarkMode = true,
                "inset 3px 3px 3px 0px #1e2126, inset -3px -3px 3px 0px rgba(61,61,69,1)",
                "12px 9px 10px rgba(13, 39, 80, 0.25), -4px -4px 8px white"
            )&";
        border-radius:25px
    '
>
</div>"

If(
    ThisItem.MenuScreenNavigate.Name = App.ActiveScreen.Name,
"<div 
    style='
        position:absolute;
        transform:translate(2px, "&Self.Height-10&"px);
        width: "&Parent.TemplateWidth -15&"px;
        height:5px;
        background:linear-gradient(to right, "&Navigation.IconLinealGradient1&", "&Navigation.IconLinealGradient2&") ;
        box-shadow: "&Navigation.IconLinealGradient2&" 0px 2px 9px,inset -3px -3px 7px "&Navigation.IconLinealGradient2&";
        border-radius: 90px' 
>
</div>",
""
)

