Rules.InvariantScript:
"<div
    style='
        position:relative;
        left:26%;
        top:0px;
        width:"&(NM_Button.X + NM_Button.Width)/2&"px;
        height:4px;
        background-image:
            linear-gradient(
                160deg,
                "& NeonGlowNavMenu.SVGActiveIconColour &" 0%,
                "& NeonGlowNavMenu.SVGActiveIconColour2 &" 100%
            );
            rgba(53,55,59,1) 81%;
        box-shadow:
            0 0 2px"& NeonGlowNavMenu.SVGActiveIconColour &",
            0 0 20px "& NeonGlowNavMenu.SVGActiveIconColour2 &",
            0 0 20px "& NeonGlowNavMenu.SVGActiveIconColour &",
            0 0 10px "& NeonGlowNavMenu.SVGActiveIconColour2 &",
            0 0 60px #80D0C7,
            0 0 20px #0093E9;
        border-radius: 5px;
'>
</div>"

"<div
    style='
        position:relative;
        justify-content:center;
        top:0px;
        width:"&Self.Width&"px;
        height:"&Self.Height-1&"px;
        background:
            "&If(
                NeonGlowNavMenu.DarkMode = true,
                "linear-gradient(135deg, rgba(36,36,44,1) 0%, rgba(52,57,61,1) 100%)",
                "linear-gradient(40deg,#f8fafd 10%,#d1d3d4 90%)"
            )&";
        box-shadow:
            "&If(
                NeonGlowNavMenu.DarkMode = true,
                "1px 1px 1px 0px rgba(61,61,69,1), 0px 0px 0px 0px #1e2126",
                "15px 15px 29px 0px rgba(43, 44, 48, 0.5),
                inset -8px -8px 16px 0px rgba(43, 44, 48, 0.4),
                inset 0px 11px 28px 0px rgb(255, 255, 255, 1)"
            )&";
        border-radius:25px 25px 0px 0px;
'>
</div>"

Table(
    {
        //this is the name of the screen
MenuItem: "Home",
        // this is the screen you will navigate to when you select this item
        MenuScreenNavigate: App.ActiveScreen,
        //if you are not comfortable with SVG's, you can use standard icons instead!  - just make sure you change the icon property of MenuIcon to ThisItem.StandardIcon
        StandardIcon: Icon.Home,
        ID: 1
    },

    {
        MenuItem: "Profile",
        MenuScreenNavigate: App.ActiveScreen,

        StandardIcon: Icon.AddUser,
        ID: 2
    },
    {
        MenuItem: "Settings",
        MenuScreenNavigate: App.ActiveScreen,
        StandardIcon: Icon.Settings,
        ID: 3
    },

    {
        MenuItem: "Balance",
        MenuScreenNavigate: App.ActiveScreen,
        StandardIcon: Icon.AddDocument,
        ID: 4
    }
)
