Rules.InvariantScript:
"<div
    style='
        position:relative;
        justify-content:center;
        top:0px;
        width:"&Self.Width&"px;
        height:"&Self.Height -1&"px;
        "&If(NeonGlowNavMenu_vertical.inversion = true,"","transform:rotate(180deg)")&";
        background:
            "&If(
                NeonGlowNavMenu_vertical.DarkMode = true,
                "linear-gradient(135deg, rgba(36,36,44,1) 0%, rgba(52,57,61,1) 100%)",
                "linear-gradient(90deg,#f8fafd 10%,#d1d3d4 90%)"
            )&";
        box-shadow:
            "&If(
                NeonGlowNavMenu_vertical.DarkMode = true,
                "1px 1px 1px 0px rgba(61,61,69,1), 0px 0px 0px 0px #1e2126",
                "
                inset -5px -4px 16px 0px rgba(43, 44, 48, 0.4),
                inset 80px 7px 25px 10px rgb(255, 255, 255, 0.2)"
            )&";
        border-radius:0px 25px 25px 0px;
'>
</div>"

"<div
    style='
        position:absolute;
        top:70px;
        "&If(NeonGlowNavMenu_vertical.inversion = true,"right:150px","left:"&Self.Width - 200 + 150&"px")&";
        width:100px;
        height:6px;
        transform:rotate(90deg);
        background-image:
            linear-gradient(
                "& NeonGlowNavMenu_vertical.SVGActiveIconColour &" 0%,
                "& NeonGlowNavMenu_vertical.SVGActiveIconColour2 &" 100%
            );
            rgba(53,55,59,1) 81%;
        box-shadow:
            0 0 2px"& NeonGlowNavMenu_vertical.SVGActiveIconColour &",
            0 0 20px "& NeonGlowNavMenu_vertical.SVGActiveIconColour2 &",
            0 0 20px "& NeonGlowNavMenu_vertical.SVGActiveIconColour &",
            0 0 10px "& NeonGlowNavMenu_vertical.SVGActiveIconColour2 &",
            0 0 60px #80D0C7,
            0 0 40px #0093E9;
        border-radius: 10px;
'>
</div>"

Table(
    {
        //this is the name of the screen
MenuItem: "Home",
        // this is the screen you will navigate to when you select this item
        MenuScreenNavigate: App.ActiveScreen,
        //if you are not comfortable with SVG's, you can use standard icons instead!  - just make sure you change the icon property of MenuIcon to ThisItem.StandardIcon
        StandardIcon: Icon.Home,
        ID: 1
    },

    {
        MenuItem: "Profile",
        MenuScreenNavigate: App.ActiveScreen,

        StandardIcon: Icon.AddUser,
        ID: 2
    },
    {
        MenuItem: "Settings",
        MenuScreenNavigate: App.ActiveScreen,
        StandardIcon: Icon.Settings,
        ID: 3
    },

    {
        MenuItem: "Balance",
        MenuScreenNavigate: App.ActiveScreen,
        StandardIcon: Icon.AddDocument,
        ID: 4
    }
)
